sp_widget.client_script:
function($scope) {
  var c = this;

	$scope.select = function(item){
		item.active = !item.active;
	};
}

sp_widget.script:
(function() {

	data.items = [];
	var columns = 10, rows = 9;
	for(var i = 0; i < rows; i++){
		var items = [];
		for(var j = 0; j < columns; j++){
			items.push({
				id: i + "x" + j,
				active: false
			});
		}
		data.items.push({
			items: items
		});
	}

	var ATTR_INACTIVE_STATES = "close_states",
   ATTR_DEFAULT_WORK = "default_work_state",
   ATTR_DEFAULT_CLOSE = "default_close_state";

	var gr = new GlideRecord("task");
	//gr.addQuery("sys_id", "f11a9122dba2a7002c7e8b4b0b9619cd");
	gr.query();
	if(gr.next()){
		console.log("Number: " + gr.getValue("number"));
		var field = gr.getElement('state');
		data.dataTEST = {
			choices: field.getChoices(),
			value: field.getAttribute("ATTR_DEFAULT_WORK")
		};
	}
})();
